mapper.select:
SELECT COUNT(*)
FROM wework_contacts 
WHERE tenant_id = #{tenantId} 
  AND contact_type = #{contactType}
  AND deleted_at IS NULL

SELECT COUNT(*)
FROM wework_contacts 
WHERE tenant_id = #{tenantId} 
  AND account_id = #{accountId}
  AND deleted_at IS NULL

SELECT COUNT(*)
FROM wework_contacts 
WHERE tenant_id = #{tenantId} 
  AND account_id = #{accountId}
  AND contact_type = #{contactType}
  AND deleted_at IS NULL

SELECT MAX(UNIX_TIMESTAMP(updated_at))
FROM wework_contacts 
WHERE tenant_id = #{tenantId} 
  AND account_id = #{accountId}
