mapper.select:
SELECT COUNT(*) FROM saas_role_permissions 
WHERE role_id = #{roleId} 
AND permission_id = #{permissionId} 
AND deleted_at IS NULL

mapper.update:
UPDATE saas_role_permissions 
SET deleted_at = NOW(), updated_at = NOW(), updated_by = #{operatorId}
WHERE role_id = #{roleId}

UPDATE saas_role_permissions 
SET deleted_at = NOW(), updated_at = NOW(), updated_by = #{operatorId}
WHERE permission_id = #{permissionId}

mapper.select:
SELECT * FROM saas_role_permissions 
WHERE role_id = #{roleId} 
AND deleted_at IS NULL

SELECT * FROM saas_role_permissions 
WHERE permission_id = #{permissionId} 
AND deleted_at IS NULL

SELECT COUNT(*) > 0 FROM saas_role_permissions 
WHERE role_id = #{roleId} 
AND permission_id = #{permissionId} 
AND deleted_at IS NULL
