mapper.select:
SELECT COUNT(*) FROM saas_user_roles 
WHERE user_id = #{userId} 
AND role_id = #{roleId} 
AND deleted_at IS NULL

mapper.update:
UPDATE saas_user_roles 
SET deleted_at = NOW(), updated_at = NOW(), updated_by = #{operatorId}
WHERE user_id = #{userId}

UPDATE saas_user_roles 
SET deleted_at = NOW(), updated_at = NOW(), updated_by = #{operatorId}
WHERE role_id = #{roleId}

mapper.select:
SELECT * FROM saas_user_roles 
WHERE user_id = #{userId} 
AND deleted_at IS NULL

SELECT * FROM saas_user_roles 
WHERE role_id = #{roleId} 
AND deleted_at IS NULL

SELECT COUNT(*) > 0 FROM saas_user_roles 
WHERE user_id = #{userId} 
AND role_id = #{roleId} 
AND deleted_at IS NULL
